#!/usr/bin/ruby
$:.unshift File.dirname(__FILE__)
$:.unshift File.join(File.dirname(__FILE__), '..', 'lib')

require 'ebay'
require 'config'

ebay = Ebay::Api.new

begin
  # Get some categories
  response = ebay.get_categories(
    :detail_level => 'ReturnAll',
    :category_parents => ['11450'],
    :level_limit => 4,
  )

  # Display the categories to the user
  clothing_categories = Array.new

  response.categories.each do |cat|
    clothing_categories << { name: cat.category_name, id: cat.category_id, level: cat.category_level }
  end

  puts "Number of categories: #{response.category_count}"
rescue Ebay::RequestError => e
  e.errors.each do |error|
    puts error.long_message
  end
end

